const crypto = require('crypto');

// Get webhook data
const webhookData = $input.first().json.body || $input.first().json;
const file = webhookData.file || {};
const fileName = file.name || 'unknown.csv';
const fileData = file.data || '';
const account_id = webhookData.account_id || null;

// Generate unique dataset ID
const timestamp = new Date().toISOString().replace(/[-:]/g, '').slice(0, 15);
const randomPart = crypto.randomBytes(4).toString('hex');
const dataset_id = `DS-${timestamp}-${randomPart}`;

// Extract dataset name
const dataset_name = fileName
  .replace(/\.csv$/i, '')
  .replace(/[^a-zA-Z0-9_-]/g, '_')
  .slice(0, 50);

// Parse CSV data
let csvContent;
if (fileData.startsWith('data:')) {
  const base64Data = fileData.split(',')[1];
  csvContent = Buffer.from(base64Data, 'base64').toString('utf-8');
} else if (Buffer.from(fileData, 'base64').toString('base64') === fileData) {
  csvContent = Buffer.from(fileData, 'base64').toString('utf-8');
} else {
  csvContent = fileData;
}

// Generate source hash
const source_hash = crypto.createHash('sha256').update(csvContent).digest('hex');

return [{
  json: {
    dataset_id,
    dataset_name,
    source_hash,
    original_filename: fileName,
    upload_timestamp: new Date().toISOString(),
    status: 'UPLOADED',
    csv_content: csvContent,
    account_id: account_id
  }
}];

// Parse CSV and normalize
const datasetInfo = $input.first().json;
const dataset_id = $('Generate Dataset ID').first().json.dataset_id;
const csvContent = $('Generate Dataset ID').first().json.csv_content;
const account_id = $('Generate Dataset ID').first().json.account_id;

function parseCSV(csv) {
  const lines = csv.trim().split('\n');
  const headers = lines[0].split(',').map(h => h.trim().toLowerCase().replace(/[^a-z0-9_]/g, '_'));
  const rows = [];

  for (let i = 1; i < lines.length; i++) {
    const values = lines[i].split(',');
    const row = {};
    headers.forEach((header, idx) => {
      row[header] = values[idx]?.trim() || '';
    });
    rows.push(row);
  }

  return { headers, rows };
}

const columnMap = {
  'property_address': ['address', 'property_address', 'street_address', 'street'],
  'city': ['city'],
  'state': ['state', 'st'],
  'zip': ['zip', 'zipcode', 'postal_code', 'zip_code'],
  'bedrooms': ['bedrooms', 'beds', 'bed', 'br'],
  'bathrooms': ['bathrooms', 'baths', 'bath', 'ba'],
  'sqft': ['sqft', 'square_feet', 'living_area', 'sq_ft'],
  'lot_sqft': ['lot_sqft', 'lot_size'],
  'year_built': ['year_built', 'yearbuilt'],
  'list_price': ['list_price', 'price', 'asking_price'],
  'mls_status': ['status', 'mls_status'],
  'agent_name': ['agent_name', 'listing_agent', 'agent'],
  'agent_email': ['agent_email', 'email'],
  'agent_phone': ['agent_phone', 'phone']
};

function mapColumns(headers) {
  const mapping = {};
  Object.keys(columnMap).forEach(canonicalField => {
    const possibleNames = columnMap[canonicalField];
    const matchedHeader = headers.find(h => possibleNames.includes(h));
    if (matchedHeader) {
      mapping[canonicalField] = matchedHeader;
    }
  });
  return mapping;
}

const parsed = parseCSV(csvContent);
const mapping = mapColumns(parsed.headers);

const normalizedRows = parsed.rows.map((row, index) => {
  const normalized = {
    row_index: index + 1,
    dataset_id: dataset_id,
    account_id: account_id
  };

  Object.keys(mapping).forEach(canonicalField => {
    const sourceField = mapping[canonicalField];
    normalized[canonicalField] = row[sourceField] || null;
  });

  return normalized;
});

return [{
  json: {
    dataset_id,
    account_id,
    total_rows: normalizedRows.length,
    normalized_rows: normalizedRows
  }
}];

const crypto = require('crypto');
const batchData = $input.first().json;
const rows = batchData.normalized_rows || [];
const dataset_id = batchData.dataset_id;
const account_id = batchData.account_id;

function validateProperty(prop) {
  const errors = [];
  const warnings = [];

  if (!prop.property_address || prop.property_address.trim() === '') errors.push('Missing address');
  if (!prop.city || prop.city.trim() === '') errors.push('Missing city');
  if (!prop.state || prop.state.trim() === '') errors.push('Missing state');
  if (!prop.zip || prop.zip.trim() === '') errors.push('Missing zip');

  if (prop.list_price && isNaN(parseFloat(prop.list_price))) {
    errors.push('Invalid list_price');
  }

  return { errors, warnings };
}

function normalizeProperty(prop) {
  if (prop.property_address) {
    prop.property_address = prop.property_address
      .trim()
      .split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
      .join(' ');
  }

  if (prop.city) {
    prop.city = prop.city.trim()
      .split(' ')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
      .join(' ');
  }

  if (prop.state) prop.state = prop.state.trim().toUpperCase().slice(0, 2);
  if (prop.zip) prop.zip = prop.zip.trim().replace(/[^0-9]/g, '').slice(0, 5);

  prop.bedrooms = prop.bedrooms ? parseInt(prop.bedrooms) : null;
  prop.bathrooms = prop.bathrooms ? parseFloat(prop.bathrooms) : null;
  prop.sqft = prop.sqft ? parseInt(prop.sqft) : null;
  prop.lot_sqft = prop.lot_sqft ? parseInt(prop.lot_sqft) : null;
  prop.year_built = prop.year_built ? parseInt(prop.year_built) : null;
  prop.list_price = prop.list_price ? parseFloat(prop.list_price) : null;

  if (prop.agent_name) {
    const nameParts = prop.agent_name.trim().split(' ');
    prop.agent_first = nameParts[0] || '';
    prop.agent_last = nameParts.slice(1).join(' ') || '';
  }

  return prop;
}

function generatePropertyId(prop, dataset_id) {
  const addressSlug = `${prop.property_address}-${prop.zip}`
    .toLowerCase()
    .replace(/[^a-z0-9]/g, '-')
    .replace(/-+/g, '-')
    .slice(0, 40);

  const hash = crypto.createHash('sha256')
    .update(`${prop.property_address}|${prop.zip}`)
    .digest('hex')
    .slice(0, 8);

  const datePrefix = dataset_id.split('-')[1].slice(4, 8);
  return `DS${datePrefix}-${addressSlug}-${hash}`;
}

function generateSourceHash(prop) {
  return crypto.createHash('sha256').update(JSON.stringify(prop)).digest('hex');
}

const validProperties = [];
const rejectedProperties = [];
const seenIds = new Set();

rows.forEach(prop => {
  const validation = validateProperty(prop);

  if (validation.errors.length > 0) {
    rejectedProperties.push({
      ...prop,
      rejection_reason: validation.errors.join('; ')
    });
  } else {
    const normalized = normalizeProperty(prop);
    const property_id = generatePropertyId(normalized, dataset_id);

    if (seenIds.has(property_id)) {
      rejectedProperties.push({
        ...normalized,
        rejection_reason: 'Duplicate within batch'
      });
    } else {
      seenIds.add(property_id);
      validProperties.push({
        property_id,
        dataset_id,
        account_id,
        ...normalized,
        source_hash: generateSourceHash(normalized),
        status: 'READY_FOR_RESEARCH',
        validation_notes: validation.warnings.join('; ') || null,
        created_at: new Date().toISOString()
      });
    }
  }
});

return [{
  json: {
    dataset_id,
    account_id,
    valid_properties: validProperties,
    rejected_properties: rejectedProperties,
    valid_count: validProperties.length,
    rejected_count: rejectedProperties.length
  }
}];

const batchData = $input.first().json;
const properties = batchData.valid_properties || [];

return properties.map(prop => ({
  json: {
    ...prop,
    dataset_id: batchData.dataset_id,
    account_id: batchData.account_id
  }
}));

const validBatchData = $('Validate & Clean').all();

let totalProcessed = 0;
let totalRejected = 0;
const allRejections = [];

validBatchData.forEach(batch => {
  const data = batch.json;
  totalProcessed += data.valid_count || 0;
  totalRejected += data.rejected_count || 0;

  if (data.rejected_properties) {
    allRejections.push(...data.rejected_properties);
  }
});

const datasetInfo = $('Generate Dataset ID').first().json;

return [{
  json: {
    dataset_id: datasetInfo.dataset_id,
    dataset_name: datasetInfo.dataset_name,
    account_id: datasetInfo.account_id,
    total_rows: totalProcessed + totalRejected,
    processed_count: totalProcessed,
    rejected_count: totalRejected,
    success_rate: ((totalProcessed / (totalProcessed + totalRejected)) * 100).toFixed(1),
    rejected_properties: allRejections
  }
}];
